import type * as jsonrpc from "json-rpc"

export function message(error: jsonrpc.Error): string {
    return errors[error.code] || error.message;
}

interface Error {
    [key: number]: string | undefined
}

const errors: Error = {
    100: "Неправильный ИД или пароль",
    103: "Доступ запрещён"
}

export enum Code {
    AccountBlocked = 102
}
