import { send } from "network";

export interface Comment {
    mandela_id: number;
    title_mode: number;
    title: string;
    what: string;
    before: string;
    after: string;
    message: string;
    user_id: number;
    user_name: string;
    create_ts: Date;
}

export interface Topic {
    id: number;
    name: string;
    post: string;
    post_count: number;
    post_create_ts: Date;
    user_id: number;
    user_name: string;
}

export namespace GetAll {
    export interface Request {
        limit: number;
    }

    export interface Response {
        comments: Comment[]
        topics: Topic[]
    }

    export async function exec(params: Request): Promise<Response[]> {
        return await send("activity.getAll", params);
    }
}
