import { send } from "network";
import type { ForumTopicType, ForumPollAnswerSelection } from "types"

export namespace GetAll {
    export interface Request {
        section_id: number;
        offset: number;
        limit: number;
    }

    export interface Topic {
        create_ts: Date;
        id: number;
        name: string;
        type: number;
        user_id: number;
        user_name: string;
    }

    export interface Response {
        category_id: number;
        category_name: string;
        section_name: string;
        topic_count: number;
        topics: Topic[];
    }

    export async function exec(params: Request): Promise<Response> {
        return await send("forum.topic.getAll", params)
    }
}

export namespace GetOne {
    export interface Request {
        id: number;
    }

    export interface Response {
        user_id: number;
        name: string;
        section_id: number;
    }

    export async function exec(params: Request): Promise<Response> {
        return await send("forum.topic.getOne", params)
    }
}

export namespace Create {
    export interface Request {
        name: string;
        section_id: number;
        type: ForumTopicType;
        poll_answers?: string[];
        poll_answer_selection?: ForumPollAnswerSelection;
    }

    export async function exec(params: Request): Promise<{}> {
        return await send("forum.topic.create", params)
    }
}

export namespace Update {
    export interface Request {
        id: number;
        name: string;
    }

    export async function exec(params: Request): Promise<{}> {
        return await send("forum.topic.update", params)
    }
}

export namespace Delete {
    export interface Request {
        id: number;
    }

    export async function exec(params: Request): Promise<{}> {
        return await send("forum.topic.delete", params)
    }
}
