import { send } from "network";

export interface Mandela {
    id?: number;
    after: string;
    before: string;
    title: string;
    title_mode: number;
    what: string;
}

export namespace GetById {
    export interface Request {
        id: number;
    }

    export type Response = Mandela;

    export async function exec(params: Request): Promise<Response> {
        return await send("search.getById", params);
    }
}

export namespace GetByContent {
    export interface Request {
        content: string;
        search_title: boolean;
        search_description: boolean;
    }

    export type Response = Mandela;

    export async function exec(params: Request): Promise<Response[]> {
        return await send("search.getByContent", params);
    }
}
