export function render(node: any): string {
    let result = "";

    node.nodes.forEach(n => {
        if (typeof n === "object") {
            result += render(n)
        } else {
            switch (node.name) {
                case "b": result += bold(n)
            }

            switch (node.name) {
                case "i": result += italic(n)
            }

            switch (node.name) {
                case "u": result += underline(n)
            }

            switch (node.name) {
                case "s": result += strikethrough(n)
            }
        }
    });

    return result;
}

export function bold(value: string): string {
    return `<strong>${value}</strong>`
}

export function italic(value: string): string {
    return `<em>${value}</em>`
}

export function underline(value: string): string {
    return `<ins>${value}</ins>`
}

export function strikethrough(value: string): string {
    return `<del>${value}</del>`
}
