export interface Request<T> {
    method: string;
    id: string;
    params?: T
}

export class RequestCreator<T> implements Request<T> {
    method: string;
    id: string;
    params?: T
    private static counter = 0;

    constructor(method: string, params?: T) {
        this.method = method;
        this.params = params;
    }

    toString(): string {
        this.id = (RequestCreator.counter++).toString();
        return JSON.stringify(this);
    }
}
