import { Request } from "json-rpc"
import { message } from "api-error"
import node_fetch from "node-fetch"
import { token } from "stores"
import { get } from 'svelte/store';

export async function send(method: string, params?: object) {
    const isBrowser = typeof window !== 'undefined';
    const fetch = isBrowser ? window.fetch : node_fetch;

    const request = new Request(method, params);

    return await fetch(`${process.env.OCEAN_API_URL}?token=${get(token)}`, {
        method: "POST",
        body: request.toString()
    }).then(r => r.text())
        .then(json => {
            const response = JSON.parse(json);

            if (response.error) {
                throw (response.error);
            } else {
                return response.result || null;
            }
        });
}

export function errorMessage(code: number): string {
    return message(code);
}

export function setToken(value: string) {
    token.set(value || process.env.ANONYM_TOKEN);
}
