export enum ScreenSize {
    Phone = 600,
    Tablet = 768,
    Desktop = 992,
    LargeDesktop = 1200,
}

export interface User {
    id: number;
    code: string;
    name: string;
    token: string;
    create_ts: any;
}

export interface Input {
    focus(): void;
}

export interface TextArea {
    selectionStart: number;
    selectionEnd: number;
    value: string;
    focus(): void;
}

export interface Session {
    user: User;
}

export interface Page {
    params: any;
    query: any;
    host: string;
    path: string;
}

export interface ActivityMessage {
    title: string,
    author: string,
    date: Date,
    message: string
    url: string
}

export interface MandelaTitle {
    title_mode: number,
    title: string,
    what: string,
    before: string,
    after: string
}

export interface ForumTopicPoll {
    id: number;
    answer: string;
    count: number;
    voted: boolean;
}

// Save non-reactive flag
export class Mounted {
    private _done = false;

    setDone() {
        this._done = true;
    }

    done() {
        return this._done;
    }
}

export enum ForumTopicType {
    Common,
    Poll
}

export enum ForumPollAnswerSelection {
    One,
    Several
}

export enum MandelaVote {
    Yes,
    No,
    Abstain
}
